options.track_layer_decision:
(function () {
  window.optimizely = window.optimizely || [];
  window.optimizely.push({
    type: 'integration',
    OAuthClientId: 19937894614
  });
  var activeExperimentIds = window.optimizely.get('state').getActiveExperimentIds();
  var experiments = window.optimizely.get('state').getExperimentStates();
  activeExperimentIds.forEach(function (expId) {
    window.zuko = window.zuko || {};
    window.zuko.attributes = window.zuko.attributes || {};
    var exp = experiments[expId];
    var expName = exp.experimentName;
    var expVariantName = exp.variation.name;
    if (expName && expVariantName) {
      window.zuko.attributes['Optimizely Experiment ' + expName] = 'Variant ' + expVariantName;
    }
  });
}());
